import React, { useContext, useEffect } from 'react'
import Nav from '../components/Nav';
import MainContext from '../context/MainContext';
import { getRamdonPersons } from '../services/SearchAPI';

function ErrorPage() {

  const { setPersons, persons } = useContext(MainContext);

  // Retirado de https://developer.mozilla.org/pt-BR/docs/Web/JavaScript/Reference/Global_Objects/Math/random
  function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
  }

  useEffect(() => {
    async function RandomPerson() {
      const { results } = await getRamdonPersons(getRandomInt(1,9));
      setPersons(results);
    }
    RandomPerson();
  }, []);

  return (
    <div>
      <Nav />
      <p>Página não encontrada</p>
      { persons.map((element) => <img src={ element.image } alt={ element.name }  />) }
    </div>
  );
}

export default ErrorPage;
