import React, { useEffect, useState } from 'react';
import Nav from '../components/Nav';
import '../css/Episode.css';
import { getEpisodes } from '../services/SearchAPI';

function Episode() {

  const [episodes, setEpisodes] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const getPersons = async () => {
     const results = await getEpisodes();
     setEpisodes(results)
    }


    getPersons() && setLoading(false);
  }, []);

  // console.log(episodes)

  function listConditinal() {
    if(loading || !episodes) {
      return <p>Carregando...</p>
    } return (
      <ul>
      {
        episodes.map((item, index) => <li key={index}> 
          <p>{item.nome}</p> 
          <p>Data de estreia: {item.dataEstreia.split([`T`[1]])} - temporada {item.temporadaId} </p>
          <p>Descrição: {item.descricao}</p>
          <p>Resumo: {item.sinopse}</p>
          </li>)
      }
    </ul>
    );
  }

  return (
    <div className="episodes">
      <Nav />
      <h1>Episódios</h1>
      <p>Em construção...</p>
      { listConditinal() }
    </div>
  );
}

export default Episode;
