import React, { useContext, useEffect } from 'react'
import Nav from '../components/Nav';
import MainContext from '../context/MainContext';
import { getRamdonPersons } from '../services/SearchAPI';
import '../css/ErrorPage.css';

function ErrorPage() {

  const { setPersons, persons } = useContext(MainContext);

  // Retirado de https://developer.mozilla.org/pt-BR/docs/Web/JavaScript/Reference/Global_Objects/Math/random
  function getRandomInt(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min)) + min;
  }2

  useEffect(() => {
    async function RandomPerson() {
      const { image } = await getRamdonPersons(getRandomInt(1,9));
      setPersons(image);
    }
    RandomPerson();
  }, []);

  console.log(getRandomInt(1,9));

  return (
    <div>
      <Nav />
      <h2>Ooooops, página não encontrada!!</h2>
      <img src={ persons } alt="random avatar from page not found" width="300px"/>
    </div>
  );
}

export default ErrorPage;
