import React, { useEffect, useState, useContext } from 'react';
import Nav from '../components/Nav';
import MainContext from '../context/MainContext';
import '../css/Home.css';

function Home() {
  const [loading, setLoading] = useState(true);
  const { 
    persons, 
    setNewPersons, 
    setSearchName, 
    setSearchStatus,
    getNewCharacter,
    getNameCharacter} = useContext(MainContext);

  useEffect(() => {
    const getPersons = () => fetch('https://rickandmortyapi.com/api/character')
  .then((initial) => (
    initial
      .json()
      .then((json) => (initial.ok ? Promise.resolve(json) : Promise.reject(json)))
  ));
    getPersons() && setLoading(false);
  }, []);

  console.log(persons);

  useEffect(() => {
    getNewCharacter() && setLoading(false);
  }, [getNewCharacter]);

  useEffect(() => {
    getNameCharacter() && setLoading(false);
  }, [getNameCharacter]);

  function listConditinal() {
    if(loading || !persons) {
      return <p>Carregando...</p>
    } return (
      <ul>
      {
          persons.map((item, index) => <li key={index}> 
            <img
              src={item.image}
              alt={item.name}
              width="300px"
            /> 
            <p>{item.name}</p>
            <p>Espécie: {item.species} </p>
            <p>Status: {item.status}</p>
            <p>Localização: {item.location.name}</p> 
            </li>)
      }
    </ul>
    );
  }

  return (
    <div>
     <Nav />
      <h1>Bem vindo ao Rick and Mort fã page</h1>
      <h3> Você pode ver mais personagens alterando os filtros</h3>
      <section>
        <input
          type="number"
          placeholder="Page number"
          onChange={ (e) => setNewPersons(e.target.value) }
          min="0"
        >
        </input>
        <input
          type="text"
          placeholder="Nome do personagem"
          onChange={ (e) => setSearchName((e.target.value).toLowerCase()) }
        >
        </input>
        <select
          onChange={ (e) => setSearchStatus(e.target.value) }
        >
          <option value="alive"> alive </option>
          <option value="dead"> dead </option>
          <option value="unknow"> unknow </option>
        </select>
      </section>
      { listConditinal() }
    </div>
  );
}

export default Home;
