import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../css/Home.css';

function Home() {

  const [persons, setPersons] = useState([]);
  const [locality, setLocalityUrl] = useState('');

  useEffect(() => {
    const getPersons = async () => {
     const endpoint = `https://rickandmortyapi.com/api/character`;
     const results = await fetch(endpoint).then((data) => data.json());
     setPersons(results)
    }

    getPersons();
  }, []);

  return (
    <div>
      <nav>
        <Link to="/">
          Home
        </Link>
        <Link to="/Episode">
          Episódios
        </Link>
        <Link to="/About">
          Sobre
        </Link>
      </nav>
      <h1>Bem vindo ao Rick and Mort fã page</h1>
      <section>
        { console.log(locality) }
      </section>
      <ul>
        {
          (persons.results).map((item, index) => <li key={index} onClick={() => setLocalityUrl(item.origin.url)}> <img src={item.image} alt={item.name} width="300px"/> <p>{item.name}</p> <p>Espécie: {item.species} </p> <p>Status: {item.status}</p> </li>)
        }
      </ul>
    </div>
  );
}

export default Home;
