import React, { useState } from 'react';
import PropTypes from 'prop-types';
import MainContext from './MainContext';
import { getNamePersons, getNewPersons, getPersons } from '../services/SearchAPI';

const Provider = ({ children }) => {
  const [persons, setPersons] = useState([]);
  const [newPersons, setNewPersons] = useState('');
  const [searchName, setSearchName] = useState('');
  const [searchStatus, setSearchStatus] = useState('');

  async function getCharacter() {
    const persons = await getPersons();
    setPersons(persons);
  }

  async function getNewCharacter() {
    const persons = await getNewPersons(newPersons);
    setPersons(persons);
  }

  async function getNameCharacter() {
    const persons = await getNamePersons(searchName);
    setPersons(persons);
  }
  const context = {
    persons,
    setPersons,
    newPersons,
    setNewPersons,
    searchName,
    setSearchName,
    searchStatus,
    setSearchStatus,
    getCharacter,
    getNewCharacter,
    getNameCharacter,
  };

  return (
    <MainContext.Provider value={ context }>
      {children}
    </MainContext.Provider>
  );
};

export default Provider;

Provider.propTypes = {
  children: PropTypes.array,
}.isRequired;
