import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../css/Home.css';

function Home() {

  const [persons, setPersons] = useState([]);
  const [locality, setLocalityUrl] = useState('');

  useEffect(() => {
    const getPersons = async () => {
     const endpoint = `https://rickandmortyapi.com/api/character`;
     const results = await fetch(endpoint).then((data) => data.json());
     setPersons(results)
    }

    getPersons();
  }, []);

  function locationConditional() {
    if(locality) {
      return (
        <section>
          <img src={locality} width="200px" alt="Location of character" />
        </section>
      )
    }
  };

  return (
    <div>
      <nav>
        <Link to="/">
          Home
        </Link>
        <Link to="/Episode">
          Episódios
        </Link>
        <Link to="/About">
          Sobre
        </Link>
      </nav>
      <h1>Bem vindo ao Rick and Mort fã page</h1>

    </div>
  );
}

export default Home;
